use nihility_common::model::get_image_text;
use nihility_config::NihilityConfigPlugin;
use nihility_log::NihilityerLogPlugin;
use nihility_model_api::NihilityApiModel;

#[tokio::test]
async fn test_image_request() {
    NihilityConfigPlugin::init("../../config").await.unwrap();
    NihilityerLogPlugin::init().await.unwrap();
    NihilityApiModel::init().await.unwrap();
    let system = r#"你将作为一个助手系统的图片识别模块,结合当前思考内容将获取的图片详细地解析并描述图片的内容;
描述内容尽可能简洁精准;
当前思考内容将由用户提供;

特别说明：你只能输出符合JSON格式的响应，且必须包含'text'字段。
Output Example:
{"text":"example text"}"#.to_string();
    let user = "详细描述这张图片".to_string();
    let image = r#"data:image/gif;base64,"#.to_string();
    let result = get_image_text(system, user, image).await;
    match result {
        Ok(value) => {
            println!("{}", value.get("text").unwrap());
        }
        Err(e) => {
            eprintln!("{:?}", e);
        }
    }
}
